import React, { useState } from "react";
import axios from "axios";

const DiseasePredictor = () => {
  const [symptoms, setSymptoms] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  const handlePredict = async () => {
    setError(""); 
    setResult(null); 

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", {
        symptoms: symptoms,
      });

      setResult(response.data);
    } catch (err) {
      setError("Error fetching prediction. Please try again.");
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Disease Prediction System</h1>
      <input
        type="text"
        placeholder="Enter symptoms, separated by commas"
        value={symptoms}
        onChange={(e) => setSymptoms(e.target.value)}
      />
      <button onClick={handlePredict}>Predict</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {result && (
        <div>
          <h2>Prediction Result</h2>
          <p><strong>Disease:</strong> {result.predicted_disease}</p>
          <p><strong>Description:</strong> {result.description}</p>
          <p><strong>Precautions:</strong> {result.precautions.join(", ")}</p>
          <p><strong>Medications:</strong> {result.medications.join(", ")}</p>
          <p><strong>Diet:</strong> {result.diet.join(", ")}</p>
          <p><strong>Workout:</strong> {result.workout.join(", ")}</p>
        </div>
      )}
    </div>
  );
};

export default DiseasePredictor;
